    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        
        Login login;
        LoginRegisterDao loginDao;
        boolean result;
        
        login = new Login();
        login.setUser(txtLogin.getText());
        
        loginDao = new LoginRegisterDao();
        result = loginDao.Connect();
        
        if(result == true) {
        
            result = loginDao.delete(login);
            
            if(result == true) {
            
                JOptionPane.showMessageDialog(null, "Game Deleted!!!");
            
            } else {
            
                JOptionPane.showMessageDialog(null, "Error when try to delete!");
                }
            } else {
                    
                    JOptionPane.showMessageDialog(null, "Connection Error!!!");
                    
                }
            
        clean();
        
    }//GEN-LAST:event_btnDeleteActionPerformed

    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        
        Login login;
        LoginRegisterDao loginDao;
        boolean result;
        
        login = new Login();
        login.setUser(txtLogin.getText());
        login.setPassword(txtPassword.getText());      
        
        loginDao = new LoginRegisterDao();
        result = loginDao.Connect();
        
        if(result == true) {
        
            result = loginDao.update(login);
            
            if(result == true) {
            
                JOptionPane.showMessageDialog(null, "Updated Data!");
                
            } else {
            
                JOptionPane.showMessageDialog(null, "Update Error!!!");
            
            }
        } else {
         
            JOptionPane.showMessageDialog(null, "Connection Error!!!");
            
        }
        
        clean();
               
    }//GEN-LAST:event_btnUpdateActionPerformed

    private void btnConsultActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConsultActionPerformed
        
        Login login;
        LoginRegisterDao loginDao;
        boolean result;
        String user;
        
        user = txtLogin.getText();
        
        loginDao = new LoginRegisterDao();
        result = loginDao.Connect();
        
        if(result == true) {
        
            login = loginDao.consult(user);
            
            if (login != null) {
            
                txtLogin.setText(login.getUser());
                txtPassword.setText(login.getPassword());
            
            } else {
            
                JOptionPane.showMessageDialog(null, "Game not found!");
            
            } 
            
        } else {
            
                    JOptionPane.showMessageDialog(null, "Connection Error!");
                    
            }
        
    }//GEN-LAST:event_btnConsultActionPerformed

